@mixin for-size($size) {
  @if $size == phone {
    @media (min-width: 320px) {
      @content;
    }
  } @else if $size == big-phone {
    @media (min-width: 480px) {
      @content;
    }
  } @else if $size == tablet {
    @media (min-width: 768px) {
      @content;
    }
  } @else if $size == desktop {
    @media (min-width: 1024px) {
      @content;
    }
  }
}

